package com.wayos.util;

import java.net.URL;

import javax.imageio.ImageIO;

import com.wayos.Configuration;

public class URLInspector {
	
	public static boolean isValid(String url) {
	    try {
	        new URL(url).toURI();
	        return true;
	    } catch (Exception e) {
	    	
	    }
	    return false;
	}
	
	/**
	 * For full domain 
     */
	public static boolean isSameDomain(String domain, String url) {
	    try {
	    	
	        String domainHost = new URL(domain).toURI().getHost();
	        String urlHost = new URL(url).toURI().getHost();
	        return domainHost.equals(urlHost);
	    } catch (Exception e) {
	    	
	    }
	    return false;
	}
	
	/**
	 * For short domain from Configuration.domain
	 * @param url
	 * @return
	 */
	public static boolean isWidget(String url) {
	    try {
	    	
	    	return url.substring(url.indexOf("://") + "://".length()).startsWith(Configuration.domain);
	    } catch (Exception e) {
	    	
	    }
	    return false;
	}
	
	
	public static boolean isImage(String url) {
			
		try {
			
			return ImageIO.read(new URL(url)) != null;
			
		} catch (Exception e) {
		}
				
		return false;
	}

	public static void main(String[] args) {
		
		String url;
		
		url = "data:image/jpeg;base64,";
		url = "data:image/jpeg;base64,";
		url = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQRqUpSReXkE9KkuXFLAtdm1tBVU6L6KbJsNQ&usqp=CAU";
		url = "data:image/jpeg;base64,";
		url = "data:image/jpeg;base64,";

		System.out.println(isValid("http://localhost:8080"));
		System.out.println(isImage("https://www.sanook.com"));
		
		System.out.println(isWidget("https://xlocalhost:8080/xxxx"));
		
		System.out.println(isImage("https://wayos.yiem.cc/public/1833768260014999/F8646F3F-AD60-4627-9D7F-A6773E15C762.jpeg"));
		
	}

}
